CREATE FUNCTION xml_transfer_bulk_upload(p_submission_id integer DEFAULT NULL::integer, p_xml_id integer DEFAULT NULL::integer, p_upload_user_id integer DEFAULT 4) RETURNS integer
    LANGUAGE plpgsql
    AS $$
Begin

	p_xml_id = Coalesce(p_xml_id, (Select Max(ID) from clearing_house.tbl_clearinghouse_xml_temp));
    
