		RETURN;
	END IF;

	IF ExistsTableColumnValues(table_name,ARRAY[
		ARRAY[field_name_1,'varchar',field_data_1],
		ARRAY[field_name_2,'integer',field_data_2::VARCHAR]
		]) IS FALSE THEN
