									field_data_1,field_name_2,field_data_2;
		RETURN;
	END IF;
	
	EXECUTE FORMAT('SELECT %s FROM %s WHERE %s = ' || quote_literal(field_data_1) 
					|| ' AND %s = ' || quote_literal(field_data_2) || ';',
					id_field_name, table_name, field_name_1, field_name_2)
